choose.when:
SELECT count(1) AS cnt
FROM ${msg_table}
WHERE STATUS = '0'
AND RESERVE_DT <= SYSDATE
AND MESSAGE_TYPE LIKE #{msg_type}
AND SEND_GROUP IS NULL

SELECT count(1) AS cnt
FROM ${msg_table}
WHERE STATUS = '0'
AND RESERVE_DT <= NOW()
AND MESSAGE_TYPE LIKE #{msg_type}
AND SEND_GROUP IS NULL

UPDATE ${msg_table}
SET STATUS = '1',
SEND_GROUP = #{group_no}
WHERE STATUS = '0'
AND RESERVE_DT <= SYSDATE
AND MESSAGE_TYPE LIKE #{msg_type}
AND SEND_GROUP IS NULL
AND ROWNUM <= 100

UPDATE ${msg_table}
SET STATUS = '1',
SEND_GROUP = #{group_no}
WHERE STATUS = '0'
AND RESERVE_DT <= NOW()
AND MESSAGE_TYPE LIKE #{msg_type}
AND SEND_GROUP IS NULL
LIMIT 100

UPDATE ${msg_table}
SET STATUS = '2',
RESPONSE_DT = SYSDATE
WHERE SEND_GROUP = #{group_no}

UPDATE ${msg_table}
SET STATUS = '2',
RESPONSE_DT = NOW()
WHERE SEND_GROUP = #{group_no}

UPDATE ${msg_table}
SET STATUS = '0',
SEND_GROUP = NULL,
RESPONSE_DT = NULL
WHERE SEND_GROUP = #{group_no}

UPDATE ${msg_table}
SET STATUS = '0',
SEND_GROUP = NULL,
RESPONSE_DT = NULL
WHERE SEND_GROUP = #{group_no}

DELETE FROM ${ml.msg_table}
WHERE MSG_ID IN

    #{msgid}

DELETE FROM ${ml.msg_table}
WHERE MSG_ID IN

    #{msgid}

SELECT count(1) AS cnt
FROM ${msg_table}
WHERE STATUS = '0'
    AND MESSAGE_TYPE LIKE #{msg_type}
    AND SEND_GROUP IS NULL

SELECT count(1) AS cnt
FROM ${msg_table}
WHERE STATUS = '0'
    AND MESSAGE_TYPE LIKE #{msg_type}
    AND SEND_GROUP IS NULL

UPDATE ${msg_table}
SET STATUS = '1',
    SEND_GROUP = #{group_no}
WHERE STATUS = '0'
    AND MESSAGE_TYPE LIKE #{msg_type}
    AND SEND_GROUP IS NULL
    AND ROWNUM <= 5

UPDATE ${msg_table}
SET STATUS = '1',
    SEND_GROUP = #{group_no}
WHERE STATUS = '0'
    AND MESSAGE_TYPE LIKE #{msg_type}
    AND SEND_GROUP IS NULL
LIMIT 5

UPDATE ${msg_table}
SET SEND_GROUP = #{img_group_no}
WHERE ROWID IN (
    SELECT rid FROM (
        SELECT ROWID rid
        FROM ${msg_table}
        WHERE STATUS = '0'
            AND RESERVE_DT <= SYSDATE
            AND MESSAGE_TYPE = #{msg_type}
            AND SEND_GROUP IS NULL
            AND FI_IMAGE_PATH IS NOT NULL
            AND IMAGE_URL IS NULL
    ) WHERE ROWNUM <= 500
)

UPDATE ${msg_table}
SET SEND_GROUP = #{img_group_no}
WHERE STATUS = '0'
    AND RESERVE_DT <= NOW()
    AND MESSAGE_TYPE = #{msg_type}
    AND SEND_GROUP IS NULL
    AND FI_IMAGE_PATH IS NOT NULL AND FI_IMAGE_PATH <> ''
    AND (IMAGE_URL IS NULL OR IMAGE_URL = '')
LIMIT 500

SELECT FI_IMAGE_PATH as bmimagepath,
    NVL(WIDE, 'N') AS WIDE
FROM ${msg_table}
WHERE SEND_GROUP = #{img_group_no}
GROUP BY FI_IMAGE_PATH, NVL(WIDE, 'N')

SELECT FI_IMAGE_PATH as bmimagepath,
    IFNULL(WIDE, 'N') AS WIDE
FROM ${msg_table}
WHERE SEND_GROUP = #{img_group_no}
GROUP BY FI_IMAGE_PATH, IFNULL(WIDE, 'N')

UPDATE ${msg_table}
SET RESULT = '${ft_image_code}',
    RESULT_DT = TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS'),
    STATUS = 'F',
    RESULT_MESSAGE = '${img_err_msg}'
WHERE SEND_GROUP = #{img_group_no}
    AND FI_IMAGE_PATH = #{mms_key}
    AND NVL(WIDE, 'N') = #{dist_value}

UPDATE ${msg_table}
SET RESULT = '${ft_image_code}',
    RESULT_DT = NOW(),
    STATUS = 'F',
    RESULT_MESSAGE = '${img_err_msg}'
WHERE SEND_GROUP = #{img_group_no}
    AND FI_IMAGE_PATH = #{mms_key}
    AND IFNULL(WIDE, 'N') = #{dist_value}

INSERT INTO ${log_table}
SELECT * FROM ${msg_table}
WHERE SEND_GROUP = #{img_group_no}
    AND FI_IMAGE_PATH = #{mms_key}
    AND NVL(WIDE, 'N') = #{dist_value}

INSERT INTO ${log_table}
SELECT * FROM ${msg_table}
WHERE SEND_GROUP = #{img_group_no}
    AND FI_IMAGE_PATH = #{mms_key}
    AND IFNULL(WIDE, 'N') = #{dist_value}

DELETE FROM ${msg_table}
WHERE SEND_GROUP = #{img_group_no}
    AND FI_IMAGE_PATH = #{mms_key}
    AND NVL(WIDE, 'N') = #{dist_value}

DELETE FROM ${msg_table}
WHERE SEND_GROUP = #{img_group_no}
    AND FI_IMAGE_PATH = #{mms_key}
    AND IFNULL(WIDE, 'N') = #{dist_value}

UPDATE ${msg_table}
SET IMAGE_URL = #{ft_image_url},
    SEND_GROUP = NULL
WHERE SEND_GROUP = #{img_group_no}
    AND FI_IMAGE_PATH = #{mms_key}
    AND NVL(WIDE, 'N') = #{dist_value}

UPDATE ${msg_table}
SET IMAGE_URL = #{ft_image_url},
    SEND_GROUP = NULL
WHERE SEND_GROUP = #{img_group_no}
    AND FI_IMAGE_PATH = #{mms_key}
    AND IFNULL(WIDE, 'N') = #{dist_value}

UPDATE ${msg_table}
SET SEND_GROUP = NULL
WHERE SEND_GROUP = #{img_group_no}
    AND FI_IMAGE_PATH = #{mms_key}
    AND NVL(WIDE, 'N') = #{dist_value}

UPDATE ${msg_table}
SET SEND_GROUP = NULL
WHERE SEND_GROUP = #{img_group_no}
    AND FI_IMAGE_PATH = #{mms_key}
    AND IFNULL(WIDE, 'N') = #{dist_value}

UPDATE ${msg_table}
SET STATUS = '1',
SEND_GROUP = #{group_no}
WHERE STATUS = '0'
    AND RESERVE_DT <= SYSDATE
    AND MESSAGE_TYPE = #{msg_type}
    AND SEND_GROUP IS NULL
    AND (FI_IMAGE_PATH IS NULL OR (IMAGE_URL IS NOT NULL AND FI_IMAGE_PATH IS NOT NULL))
    AND ROWNUM <= 100

UPDATE ${msg_table}
SET STATUS = '1',
SEND_GROUP = #{group_no}
WHERE STATUS = '0'
    AND RESERVE_DT <= NOW()
    AND MESSAGE_TYPE = #{msg_type}
    AND (FI_IMAGE_PATH IS NULL OR FI_IMAGE_PATH = '' OR (IMAGE_URL IS NOT NULL AND FI_IMAGE_PATH IS NOT NULL))
    AND SEND_GROUP IS NULL
LIMIT 100
