choose.when:
SELECT count(1) AS cnt
FROM ${msg_table}
WHERE STATUS = '0'
    AND RESERVE_DT <= SYSDATE
    AND MESSAGE_TYPE LIKE #{msg_type}
    AND SEND_GROUP IS NULL

SELECT count(1) AS cnt
FROM ${msg_table}
WHERE STATUS = '0'
    AND RESERVE_DT <= NOW()
    AND MESSAGE_TYPE LIKE #{msg_type}
    AND SEND_GROUP IS NULL

UPDATE ${msg_table}
SET STATUS = '1',
    SEND_GROUP = #{group_no}
WHERE STATUS = '0'
    AND RESERVE_DT <= SYSDATE
    AND MESSAGE_TYPE LIKE #{msg_type}
    AND SEND_GROUP IS NULL
    AND ROWNUM <= 100

UPDATE ${msg_table}
SET STATUS = '1',
    SEND_GROUP = #{group_no}
WHERE STATUS = '0'
    AND RESERVE_DT <= NOW()
    AND MESSAGE_TYPE LIKE #{msg_type}
    AND SEND_GROUP IS NULL
LIMIT 100

UPDATE ${msg_table}
    SET STATUS = '2',
    RESPONSE_DT = SYSDATE
WHERE SEND_GROUP = #{group_no}

UPDATE ${msg_table}
    SET STATUS = '2',
    RESPONSE_DT = NOW()
WHERE SEND_GROUP = #{group_no}

UPDATE ${msg_table}
SET STATUS = '0',
    SEND_GROUP = NULL,
    RESPONSE_DT = NULL
WHERE SEND_GROUP = #{group_no}

UPDATE ${msg_table}
SET STATUS = '0',
    SEND_GROUP = NULL,
    RESPONSE_DT = NULL
WHERE SEND_GROUP = #{group_no}
