choose.when:
SELECT count(1) AS cnt
FROM ${msg_table}
WHERE STATUS = '0'
    AND RESERVE_DT <= SYSDATE
    AND MESSAGE_TYPE = #{msg_type}
    AND SEND_GROUP IS NULL

SELECT count(1) AS cnt
FROM ${msg_table}
WHERE STATUS = '0'
    AND RESERVE_DT <= NOW()
    AND MESSAGE_TYPE = #{msg_type}
    AND SEND_GROUP IS NULL

UPDATE ${msg_table}
SET STATUS = '1',
    SEND_GROUP = #{group_no}
WHERE STATUS = '0'
    AND RESERVE_DT <= SYSDATE
    AND MESSAGE_TYPE = #{msg_type}
    AND SEND_GROUP IS NULL
    AND ROWNUM <= 200

UPDATE ${msg_table}
SET STATUS = '1',
    SEND_GROUP = #{group_no}
WHERE STATUS = '0'
    AND RESERVE_DT <= NOW()
    AND MESSAGE_TYPE = #{msg_type}
    AND SEND_GROUP IS NULL
LIMIT 200

UPDATE ${msg_table}
SET STATUS = '2',
    RESPONSE_DT = SYSDATE
WHERE SEND_GROUP = #{group_no}

UPDATE ${msg_table}
SET STATUS = '2',
    RESPONSE_DT = NOW()
WHERE SEND_GROUP = #{group_no}

UPDATE ${msg_table}
SET STATUS = '0',
    SEND_GROUP = NULL,
    RESPONSE_DT = NULL
WHERE SEND_GROUP = #{group_no}

UPDATE ${msg_table}
SET STATUS = '0',
    SEND_GROUP = NULL,
    RESPONSE_DT = NULL
WHERE SEND_GROUP = #{group_no}

UPDATE ${msg_table}
SET STATUS = #{status, jdbcType=VARCHAR},
    RESULT_DT = #{result_dt, jdbcType=VARCHAR},
    RESULT = #{s_code, jdbcType=VARCHAR},
    RESULT_MSG = #{code, jdbcType=VARCHAR},
    RESULT_MESSAGE = #{result_message, jdbcType=VARCHAR},
    TELCOINFO = #{telecom, jdbcType=VARCHAR},
    REAL_MESSAGE_TYPE = #{real_send_type, jdbcType=VARCHAR},
    BCAST_CNT = #{bcast_cnt, jdbcType=VARCHAR}
WHERE MSG_ID = #{msgid, jdbcType=VARCHAR}

UPDATE ${msg_table}
SET STATUS = #{status},
    RESULT_DT = #{result_dt},
    RESULT = #{s_code},
    RESULT_MSG = #{code},
    RESULT_MESSAGE = #{result_message},
    TELCOINFO = #{telecom},
    REAL_MESSAGE_TYPE = #{real_send_type},
    BCAST_CNT = #{bcast_cnt}
WHERE MSG_ID = #{msgid}

SELECT COUNT(1) as cnt
FROM ${msg_table}
WHERE STATUS = '9'

SELECT COUNT(1) as cnt
FROM ${msg_table}
WHERE STATUS = '9'

UPDATE ${msg_table}
SET SEND_GROUP = #{group_no}
WHERE STATUS = '9'
    AND ROWNUM < 200

UPDATE ${msg_table}
SET SEND_GROUP = #{group_no}
WHERE STATUS = '9'
LIMIT 200
